-- View: client_destinations_dialogDROP VIEW client_destinations_dialog;CREATE OR REPLACE VIEW client_destinations_dialog AS 	SELECT		d.id,		d.client_id,		d.region,		d.region_code,		d.raion,		d.raion_code,		d.gorod,		d.gorod_code,		d.naspunkt,		d.naspunkt_code,		d.ulitza,		d.ulitza_code,		d.dom,		d.korpus,		d.kvartira,		d.addr_index,		replace(replace(st_astext(ST_Centroid(d.zone_center)),'POINT(',''),')','') AS zone_center_str		/*		replace(replace(st_astext(d.zone),'POLYGON((',''),'))','') AS zone_str,		replace(replace(st_astext(ST_Centroid(d.zone)),'POINT(',''),')','') AS zone_center_str		*/	FROM client_destinations AS d	;ALTER TABLE client_destinations_dialog OWNER TO polimerplast;