-- View: client_list--DROP VIEW client_list;CREATE OR REPLACE VIEW client_list AS 	SELECT 		cl.id,		cl.name,		cl.client_activity_id,		cl_a.name AS client_activity_descr,		cl.inn,		get_contract_states_descr(cl_c.state) AS contract_descr,		date10_descr(cl_c.date_to) contract_period,		CASE			WHEN cl.pay_type='with_delay'::payment_types THEN				get_payment_types_descr(cl.pay_type)::text||'('||cl.pay_delay_days||'дн.)'			ELSE				get_payment_types_descr(cl.pay_type)::text		END AS terms,		0 AS debt,		cl.login_allowed,		cl.def_firm_id,		cl.def_warehouse_id,		f.name AS def_firm_descr,		w.name AS def_warehouse_descr,				cld.debt_total,		cld.def_debt						FROM clients AS cl	LEFT JOIN client_activities AS cl_a ON cl_a.id=cl.client_activity_id		LEFT JOIN firms f ON f.id=cl.def_firm_id	LEFT JOIN warehouses w ON w.id=cl.def_warehouse_id		LEFT JOIN client_contracts AS cl_c		ON cl_c.client_id=cl.id		AND cl_c.date_to=(			SELECT MAX(sub.date_to)			FROM client_contracts AS sub			WHERE sub.client_id=cl.id			)				LEFT JOIN (		SELECT			t.client_id,			sum(t.debt_total) AS debt_total,			sum(t.def_debt) AS def_debt		FROM client_debts AS t		GROUP BY t.client_id		) cld ON cld.client_id=cl.id				WHERE cl.registered;ALTER TABLE client_list OWNER TO polimerplast;