-- View: doc_orders_shipment_dialog--DROP VIEW doc_orders_shipment_dialog;CREATE OR REPLACE VIEW doc_orders_shipment_dialog AS 	SELECT		d.id,						d.date_time,		date8_time5_descr(d.date_time) AS date_time_descr,				d.number,		d.processed,				d.client_id,		cl.name AS client_descr,		cl.pay_type					FROM doc_orders AS d	LEFT JOIN clients AS cl ON cl.id=d.client_id	LEFT JOIN users AS u ON u.id=d.user_id	;ALTER TABLE doc_orders_shipment_dialog OWNER TO polimerplast;